(function(){
  // Loader: attempt to load Chart.js UMD directly from node_modules so renderer can use ./vendor/chart.min.js
  // This keeps the vendor file small while still providing an offline path in development.
  try {
    var src = '../../node_modules/chart.js/dist/chart.umd.min.js';
    var s = document.createElement('script');
    s.type = 'text/javascript';
    s.async = false;
    s.src = src;
    s.addEventListener('error', function(){
      console.error('vendor/chart.min.js: failed to load Chart.js from', src);
    });
    document.head.appendChild(s);
  } catch (e) {
    console.error('vendor/chart.min.js loader error', e);
  }
})();
